import { useRef, useState } from 'react';
import { Form } from './styles';
import Modal from '../Modal';

const ModalCallingPriceSimulator = (props) => {

  const {isOpen, setIsOpen, handleNext} = props; 
  const [formStep, setFormStep] = useState(0);
  const completeFormStep = () => {
    setFormStep(cur => cur + 1)
  }
  const goBackFormStep = () => {
    setFormStep(cur => cur - 1)
  }

  const finalFormStep = () => {
    //the result is x
  }

  const renderButton = () => {

    if (formStep < 1){
      return(
        <button onClick={completeFormStep} type="button">
              <p className="text">Continuar</p>
        </button>
      )
    }else if (formStep === 1 && formStep <= 3){
      return(
        <div>
        <button onClick={goBackFormStep} type="button">
        <p className="text">Voltar</p>
        </button>

        <button onClick={completeFormStep} type="button">
        <p className="text">Continuar</p>
        </button>
        </div>
      )
    }

  }

  const formRef = useRef(null);

  async function handleSubmit (data) {
    handleNext(data);
    setIsOpen();
  };

    return (
      <Modal isOpen={isOpen} setIsOpen={setIsOpen}>
        <Form ref={formRef} onSubmit={handleSubmit}>

          { formStep === 0 && (<section>
            <h2>Origem e Destino da sua Ligação</h2>

            <div class="form-group">
              <input type="radio" id="011-016" name="origin-destiny"/>
              <label for="011-016">(011) {'>>'} (016)</label><br></br>
            </div>

            <div class="form-group">
              <input type="radio" id="011-017" name="origin-destiny"/>
              <label for="011-017">(011) {'>>'} (017)</label><br></br>
            </div>

            <div class="form-group">
              <input type="radio" id="011-018" name="origin-destiny"/>
              <label for="011-018">(011) {'>>'} (018)</label><br></br>
            </div>

            <div class="form-group">
              <input type="radio" id="016-011" name="origin-destiny"/>
              <label for="016-011">(016) {'>>'} (011)</label><br></br>
            </div>

            <div class="form-group">
              <input type="radio" id="017-011" name="origin-destiny"/>
              <label for="017-011">(017) {'>>'} (011)</label><br></br>
            </div>

            <div class="form-group">
              <input type="radio" id="018-011" name="origin-destiny"/>
              <label for="018-011">(018) {'>>'} (011)</label><br></br>
            </div>

          </section>)}

          { formStep === 1 && (
          <section>

          <h2>Tempo de Duração da sua Ligação</h2>

          <div class="form-group">
            <input type="text" id="time-duration-call" name="time-duration-call" placeholder="Insira o tempo da sua ligação aqui =)"/>
          </div>

          </section>
          )}

          { formStep === 2 && (
          <section><h2>Qual é o plano do seu interesse?</h2>

          <div class="form-group">
            <input type="radio" id="falemais-30" name="falemais"/>
            <label for="falemais-30">FaleMais 30</label><br></br>
          </div>

          <div class="form-group">
            <input type="radio" id="falemais-60" name="falemais"/>
            <label for="falemais-60">FaleMais 60</label><br></br>
          </div>

          <div class="form-group">
            <input type="radio" id="falemais-120" name="falemais"/>
            <label for="falemais-120">FaleMais 120</label><br></br>
          </div>

          </section>)}


          { formStep === 3 && (
          <section>
            <h2>Resumo</h2>
            <div>
              Origem: XX  Destino: XX <br/>
              <hr/>
              <br/>
              Tempo de duração da ligação: 00:00
              <br/>
              Plano: FaleMais XX
              <br/>

            </div>
          </section>)}

          { formStep === 4 && (
          <section>
            <h2>Com o FaleMais</h2>
            <div>
              R$ XX,XX 
            </div> 
            <br/>

            <h2>Sem o FaleMais</h2>
            <div>
              R$ XX,XX 
            </div> 
          </section>)}





          <button onSubmit={finalFormStep} type="button">
              <p className="text">Calcular</p>
          </button>

        </Form>
      </Modal>

    );
}


export default ModalCallingPriceSimulator;
