import { createContext, ReactNode, useContext, useState, useRef, useEffect } from 'react';
import { toast } from 'react-toastify';

const phoneCallsData = {

    originDestiny:'',
    timeDurationCall:0,
    planName:''

}

const CallsContext = createContext(phoneCallsData);

export function CallsProvider({children}){

  const [minuteValue, setMinuteValue] = useState(0);
  const [originDestiny, setOriginDestiny] = useState('');
  const [timeDurationCall, setTimeDurationCall] = useState(0);
  const [planName, setPlanName] = useState('');
  const [planValue, setPlanValue] = useState(0);
  const [resultWithFaleMais, setResultWithFaleMais] = useState(0);
  const [resultWithoutFaleMais, setResultWithoutFaleMais] = useState(0);

 function handleCalculate(dataOriginDestiny,dataDurationCall,dataPlan){

    setOriginDestiny(dataOriginDestiny);
    setTimeDurationCall(dataDurationCall);
    setPlanName(dataPlan);

    const CallingMinuteValue = () =>{

      switch(originDestiny){
         case '011-016':
           return ('1.90');

         case '011-017':
           return ('1.70');

         case '011-018':
           return ('0.90');

         case '017-011':
           return ('2.70');

         case '018-011':
           return ('1.90');

         default:
            return (0);
      }

    }

    setMinuteValue(parseFloat(CallingMinuteValue));
    console.log(minuteValue);
    console.log(originDestiny);
    console.log(timeDurationCall);
    console.log(planName);

    //** VXTel Plan FaleMais Minute Value **//

    const VXTelPlan = () => {
      switch(planValue){
         case 'falemais-30':
           return (30);

         case 'falemais-60':
           return (60);

         case 'falemais-120':
           return (120);

         default:
           return (0);
      }
    }

    setPlanValue(VXTelPlan);
    console.log(planValue);

    //** VXTel Plan FaleMais Name Value **//

      const VXTelPlanName = () => {
        switch(planValue){
           case 'falemais-30':
             return ('FaleMais 30');

           case 'falemais-60':
             return ('FaleMais 60');

           case 'falemais-120':
             return ('FaleMais 120');

           default:
           return ('');
        }
      }

      setPlanName(VXTelPlanName);
      console.log(planName);


    //** Result with FaleMais **//

    if (minuteValue <= planValue) {
      const resultWithFaleMais = 0.00;
      setResultWithFaleMais(resultWithFaleMais);
    }else{
      const remainingMinutes = planValue - minuteValue;
      const newMinuteValue = minuteValue + (minuteValue/10);
      const resultWithFaleMais = remainingMinutes * newMinuteValue;
      return resultWithFaleMais;
    }

    //** Result without FaleMais **//
    setResultWithoutFaleMais(timeDurationCall * minuteValue);

    console.log(resultWithFaleMais);
    console.log(resultWithoutFaleMais);
  }

  return (
    <CallsContext.Provider
      value={{ handleCalculate, timeDurationCall, originDestiny, planName, resultWithFaleMais, resultWithoutFaleMais }}
    >
      {children}
    </CallsContext.Provider>
  );

}

export function useCalls(){
  const context = useContext(CallsContext);

  return context;
}
