import { React, useState } from 'react'
import {useForm} from 'react-hook-form'

import { Form } from './styles'
import Modal from '../Modal'
import { useCalls } from '../../hooks/useCalls';


const ModalCallingPriceSimulator = (props) => {

  const {isOpen, setIsOpen} = props; 
  const { 
    watch, 
    register
   }  = useForm();

  const [formStep,setFormStep] = useState(0);

  const { handleCalculate, timeDurationCall, resultWithFaleMais, resultWithoutFaleMais } = useCalls();

  const completeFormStep = () => {
    setFormStep(cur => cur + 1)
    console.log(formStep)
  }
  const goBackFormStep = () => {
    setFormStep(cur => cur - 1)
  }

  const handleNext = () => {
    completeFormStep();
  }

  const handleBack = () => {
    goBackFormStep();
  }

  const renderButton = () => {

    if (formStep < 1){
      return(
        <button onClick={handleNext} type="button">
              <p className="text">Continuar</p>
        </button>
      )
    }else if (formStep === 1 || formStep <= 2){
      return(
        <div>
        <button onClick={handleBack} type="button">
        <p className="text">Voltar</p>
        </button>

        <button onClick={handleNext} type="button">
        <p className="text">Continuar</p>
        </button>
        </div>
      )
    }else if (formStep > 2){
      return (
        <div>
           <button onClick={handleBack} type="button">
              <p className="text">Voltar</p>
           </button>

          <button onSubmit={handleSubmit} type="button">
              <p className="text">Calcular</p>
          </button>
        </div>
      );
    }

  }


  async function handleSubmit(data) {
    handleCalculate(data);
  };

    return (
      <Modal isOpen={isOpen} setIsOpen={setIsOpen}>
        <Form>

          { formStep === 0 && (
          <section>
            <h2>Origem e Destino da sua Ligação</h2>

            <div className="form-group">
              <input type="radio" id="011-016" name="origin-destiny" value="011-016"/>
              <label htmlFor="011-016">(011) {'>>'} (016)</label><br></br>
            </div>

            <div className="form-group">
              <input type="radio" id="011-017" name="origin-destiny" value="011-017"/>
              <label htmlFor="011-017">(011) {'>>'} (017)</label><br></br>
            </div>

            <div className="form-group">
              <input type="radio" id="011-018" name="origin-destiny" value="011-018"/>
              <label htmlFor="011-018">(011) {'>>'} (018)</label><br></br>
            </div>

            <div className="form-group">
              <input type="radio" id="016-011" name="origin-destiny" value="016-011"/>
              <label htmlFor="016-011">(016) {'>>'} (011)</label><br></br>
            </div>

            <div className="form-group">
              <input type="radio" id="017-011" name="origin-destiny" value="017-011"/>
              <label htmlFor="017-011">(017) {'>>'} (011)</label><br></br>
            </div>

            <div className="form-group">
              <input type="radio" id="018-011" name="origin-destiny" value="018-011"/>
              <label htmlFor="018-011">(018) {'>>'} (011)</label><br></br>
            </div>

          </section>)}

          { formStep === 1 && (
          <section>

          <h2>Tempo de Duração da sua Ligação</h2>

          <div className="form-group">
            <input 
            type="text" 
            id="time-duration-call" 
            name="time-duration-call" 
            placeholder="Insira o tempo da sua ligação(em minutos)  aqui =)" 
            {...register('time-duration-call', {required:{
              value:true, 
              message: 'Por favor, coloque o tempo de duração da chamada',
            },
            })}
            />
          </div>

          </section>
          )}

          { formStep === 2 && (
          <section><h2>Qual é o plano do seu interesse?</h2>

          <div className="form-group">
            <input type="radio" id="falemais-30" name="falemais" value="falemais-30"/>
            <label htmlFor="falemais-30">FaleMais 30</label><br></br>
          </div>

          <div className="form-group">
            <input type="radio" id="falemais-60" name="falemais" value="falemais-60"/>
            <label htmlFor="falemais-60">FaleMais 60</label><br></br>
          </div>

          <div className="form-group">
            <input type="radio" id="falemais-120" name="falemais" value="falemais-120"/>
            <label htmlFor="falemais-120">FaleMais 120</label><br></br>
          </div>

          </section>)}


          { formStep === 3 && (
          <section>
            <h2>Resumo</h2>
            <div>
              Origem - Destino: {"origin-destiny"} <br/>
              <hr/>
              <br/>
              Tempo de duração da ligação: {"time-duration-call"}
              <br/>
              Plano: {"falemais"}
              <br/>

              <h2>Com o FaleMais</h2>
              <div>
              R$ {"resultWithFaleMais"}
              </div> 
              <br/>

              <h2>Sem o FaleMais</h2>
              <div>
                R$ {"resultWithoutFaleMais"}
              </div> 

            </div>
          </section>)}

          {renderButton()}
          <pre>{JSON.stringify(watch(), null, 2)}</pre>

        </Form>
      </Modal>

    );
}


export default ModalCallingPriceSimulator;
