import { createContext, ReactNode, useContext, useState, useRef, useEffect } from 'react';
import { toast } from 'react-toastify';

const CallsContext = createContext;

export function CallsProvider({children}){

  const [minuteValue, setMinuteValue] = useState(0);
  const [timeDurationCall, setTimeDurationCall] = useState(0);
  const [planValue, setPlanValue] = useState(0);

  const [resultWithFaleMais, setResultWithFaleMais] = useState(0);
  const [resultWithoutFaleMais, setResultWithoutFaleMais] = useState(0);

 const handleCalculate = (data) => {

    const CallingMinuteValue = (data) =>{

      switch(data.origin-destiny){
         case '011-016':
           return ('1.90');

         case '011-017':
           return ('1.70');

         case '011-018':
           return ('0.90');

         case '017-011':
           return ('2.70');

         case '018-011':
           return ('1.90');
      }

    }

    setMinuteValue(parseFloat(CallingMinuteValue));

    const VXTelPlan = (data) => {
      switch(data.falemais){
         case 'falemais-30':
           return (30);

         case 'falemais-60':
           return (60);

         case 'falemais-120':
           return (120);
      }
    }

    setTimeDurationCall(data.time-duration-call);

    setPlanValue(VXTelPlan);

    //with FaleMais//
    if (minuteValue <= planValue) {
      const resultWithFaleMais = 0.00;
      setResultWithFaleMais(resultWithFaleMais);
    }else{
      const remainingMinutes = planValue - minuteValue;
      const newMinuteValue = minuteValue + (minuteValue/10);
      const resultWithFaleMais = remainingMinutes * newMinuteValue;
      return resultWithFaleMais;
    }

    //without Falemais//
    setResultWithoutFaleMais(timeDurationCall * minuteValue);
  }

}
