import { Component, createRef } from 'react';

import { Form } from './styles';
import Modal from '../Modal';

class ModalCallingPriceSimulator extends Component {
  constructor(props) {
    super(props);
    this.formRef = createRef();
  }

  handleSubmit = async data => {
    const { setIsOpen, handleAddFood } = this.props;

    handleAddFood(data);
    setIsOpen();
  };

  render() {
    const { isOpen, setIsOpen } = this.props;

    return (
      <Modal isOpen={isOpen} setIsOpen={setIsOpen}>
        <Form ref={this.formRef} onSubmit={this.handleSubmit}>
          <h1>Origem e Destino da sua Ligação</h1>

          <div class="form-group">
            <input type="radio" id="011-016" name="origin-destiny"/>
            <label for="011-016">(011) {'>>'} (016)</label><br></br>
          </div>

          <div class="form-group">
            <input type="radio" id="011-017" name="origin-destiny"/>
            <label for="011-017">(011) {'>>'} (017)</label><br></br>
          </div>

          <div class="form-group">
            <input type="radio" id="011-018" name="origin-destiny"/>
            <label for="011-018">(011) {'>>'} (018)</label><br></br>
          </div>

          <div class="form-group">
            <input type="radio" id="016-011" name="origin-destiny"/>
            <label for="016-011">(016) {'>>'} (011)</label><br></br>
          </div>

          <div class="form-group">
            <input type="radio" id="017-011" name="origin-destiny"/>
            <label for="017-011">(017) {'>>'} (011)</label><br></br>
          </div>

          <div class="form-group">
            <input type="radio" id="018-011" name="origin-destiny"/>
            <label for="018-011">(018) {'>>'} (011)</label><br></br>
          </div>

          <button type="submit" data-testid="origin-destiny-button">
            <p className="text">Continuar</p>
          </button>

        </Form>
      </Modal>
    );
  }
};

export default ModalCallingPriceSimulator;
