import { React, useState } from 'react'
import {useForm} from 'react-hook-form'

import { Form } from './styles'
import Modal from '../Modal'


const ModalCallingPriceSimulator = (props) => {

  const {isOpen, setIsOpen} = props; 
  const { 
    watch, 
    register
   }  = useForm();

  const [formStep,setFormStep] = useState(0);

  const [originDestiny, setOriginDestiny] = useState('');
  const [timeDurationCall, setTimeDurationCall] = useState(0);
  const [planName, setPlanName] = useState('');

  const [minuteValue, setMinuteValue] = useState(0);
  const [planValue, setPlanValue] = useState(0);

  const [resultWithFaleMais, setResultWithFaleMais] = useState(0);
  const [resultWithoutFaleMais, setResultWithoutFaleMais] = useState(0);

  const completeFormStep = () => {
    setFormStep(cur => cur + 1)
    console.log(formStep)
  }
  const goBackFormStep = () => {
    setFormStep(cur => cur - 1)
  }

  const handleNext = () => {
    completeFormStep();
  }

  const handleBack = () => {
    goBackFormStep();
  }

  function handleOriginDestinyChanged(e) {
    setOriginDestiny(e.target.value);
  }

  function handleTimeDurationCallChanged(e) {
    setTimeDurationCall(e.target.value);
  }

  function handlePlanChanged(e) {
    setPlanName(e.target.value);
  }

  async function handleSubmit(dataOriginDestiny,dataDurationCall,dataPlan) {

    const handleCalculate = async (dataOriginDestiny,dataDurationCall,dataPlan) => {

      setOriginDestiny(dataOriginDestiny);
      setTimeDurationCall(dataDurationCall);
      setPlanName(dataPlan);

      const CallingMinuteValue = () => {

        switch(OriginDestiny){
           case '011-016':
             return ('1.90');

           case '011-017':
             return ('1.70');

           case '011-018':
             return ('0.90');

           case '017-011':
             return ('2.70');

           case '018-011':
             return ('1.90');

           default:
              return (0);
        }

      }

      setMinuteValue(parseFloat(CallingMinuteValue));
      console.log(minuteValue);
      console.log(newOriginDestiny);
      console.log(newTimeDurationCall);
      console.log(newPlanName);

      //** VXTel Plan FaleMais Minute Value **//

      const VXTelPlan = () => {
        switch(planValue){
           case 'falemais-30':
             return (30);

           case 'falemais-60':
             return (60);

           case 'falemais-120':
             return (120);

           default:
             return (0);
        }
      }

      setPlanValue(VXTelPlan);
      console.log(planValue);

      //** VXTel Plan FaleMais Name Value **//

        const VXTelPlanName = () => {
          switch(planValue){
             case 'falemais-30':
               return ('FaleMais 30');

             case 'falemais-60':
               return ('FaleMais 60');

             case 'falemais-120':
               return ('FaleMais 120');

             default:
             return ('');
          }
        }

        setNewPlanName(VXTelPlanName);
        console.log(newPlanName);


      //** Result with FaleMais **//

      if (minuteValue <= planValue) {
        const resultWithFaleMais = 0.00;
        setResultWithFaleMais(resultWithFaleMais);
      }else{
        const remainingMinutes = planValue - minuteValue;
        const newMinuteValue = minuteValue + (minuteValue/10);
        const resultWithFaleMais = remainingMinutes * newMinuteValue;
        return resultWithFaleMais;
      }

      //** Result without FaleMais **//
      setResultWithoutFaleMais(newTimeDurationCall * minuteValue);

      console.log(resultWithFaleMais);
      console.log(resultWithoutFaleMais);
    }
  };

  const renderButton = () => {

    if (formStep < 1){
      return(
        <button onClick={handleNext} type="button">
              <p className="text">Continuar</p>
        </button>
      )
    }else if (formStep === 1 || formStep <= 2){
      return(
        <div>
        <button onClick={handleBack} type="button">
        <p className="text">Voltar</p>
        </button>

        <button onClick={handleNext} type="button">
        <p className="text">Continuar</p>
        </button>
        </div>
      )
    }else if (formStep > 2){
      return (
        <div>
           <button onClick={handleBack} type="button">
              <p className="text">Voltar</p>
           </button>

          <button onClick={() => handleSubmit(originDestiny,timeDurationCall,planName)} type="button">
              <p className="text">Calcular</p>
          </button>

        </div>
      );
    }

  }



    return (
      <Modal isOpen={isOpen} setIsOpen={setIsOpen}>
        <Form>

          { formStep === 0 && (
          <section>
            <h2>Origem e Destino da sua Ligação</h2>

            <div className="form-group">
              <input type="radio" id="011-016" name="origin-destiny" value="011-016" onChange={handleOriginDestinyChanged.bind(this)}/>
